import re 
import datetime 


def parse_historical_memory(memory_string: str) -> tuple[str, datetime.datetime | None]:
    """
    Parses a memory string that might start with a timestamp.
    Expected format: "[DD Mon YYYY HH:MM:SS] Actual memory text."
    Returns the actual memory text (without the timestamp prefix) and 
    the parsed datetime object. If no valid timestamp prefix is found,
    returns the original string and None for the timestamp.
    """
    memory_string = memory_string.strip()
    # Regex to capture the first bracketed timestamp and the rest of the string
    match = re.match(r"^\s*\[(.*?)\]\s*(.*)", memory_string)
    if match:
        timestamp_str = match.group(1)
        text_content = match.group(2).strip() # The actual memory text
        try:
            # Attempt to parse the captured timestamp string
            dt_obj = datetime.datetime.strptime(timestamp_str, "%d %b %Y %H:%M:%S")
            return text_content, dt_obj
        except ValueError:
            # print(f"Warning: Could not parse timestamp string '{timestamp_str}' from memory: '{memory_string[:100]}...'")
            pass # Fall through to return original string and None
            
    return memory_string, None

